    private void btCadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btCadastrarActionPerformed
        // Inicia um bloco de c√≥digo protegido. Qualquer erro (exce√ß√£o) que ocorrer aqui dentro
// ser√° capturado pelo bloco 'catch' correspondente, evitando que o programa quebre.
        try {
            // --- SE√á√ÉO 1: COLETA E VALIDA√á√ÉO DOS DADOS DA TELA ---

            // Obt√©m o texto digitado pelo usu√°rio no campo de nome.
            String novoNome = txtNome.getText();
            // Obt√©m o texto digitado no campo de doen√ßas.
            String novoDoencas = txtDoencas.getText();
            // Obt√©m o texto digitado no campo de medica√ß√£o.
            String novoMedicacao = txtMedicacao.getText();
            // Obt√©m o texto digitado no campo de hist√≥rico m√©dico.
            String novoHistoricoMedico = txtHistorico.getText();

            // Obt√©m o texto digitado no campo de peso. √â uma String por enquanto.
            String novoPesoStr = txtPeso.getText();
            // Converte a String de peso para um n√∫mero do tipo 'double'.
            // A fun√ß√£o 'replace' troca v√≠rgulas por pontos para evitar erros de formato.
            double novoPeso = Double.parseDouble(novoPesoStr.replace(",", "."));

            // Obt√©m o texto digitado no campo de ID do tutor. Tamb√©m √© uma String.
            String novoIdTutorStr = txtIdTutor.getText();
            // Converte a String de ID do tutor para um n√∫mero do tipo 'int'.
            int novoIdTutor = Integer.parseInt(novoIdTutorStr);

            // Valida√ß√£o para garantir que os campos essenciais n√£o est√£o vazios.
            // O m√©todo 'trim()' remove espa√ßos em branco no in√≠cio e no fim antes de verificar.
            if (novoNome.trim().isEmpty() || novoPesoStr.trim().isEmpty() || novoDoencas.trim().isEmpty()
                    || novoMedicacao.trim().isEmpty() || novoHistoricoMedico.trim().isEmpty()
                    || novoIdTutorStr.trim().isEmpty()) {

                // Se algum campo estiver vazio, exibe uma janela de di√°logo com uma mensagem de erro.
                JOptionPane.showMessageDialog(this, "Todos os campos obrigat√≥rios devem ser preenchidos!",
                        "Erro de Valida√ß√£o", JOptionPane.ERROR_MESSAGE);
                // A palavra-chave 'return' interrompe a execu√ß√£o do m√©todo imediatamente.
                return;
            }

            // --- SE√á√ÉO 2: L√ìGICA DE NEG√ìCIO E PERSIST√äNCIA ---
            // Cria uma nova inst√¢ncia da classe Pets, um objeto "vazio" pronto para ser preenchido.
            Pets novoPet = new Pets();
            // Define o nome do novo pet com o valor obtido da tela.
            novoPet.setNome(novoNome);
            // Define o peso do novo pet.
            novoPet.setPeso(novoPeso);
            // Define as doen√ßas do novo pet.
            novoPet.setDoencas(novoDoencas);
            // Define a medica√ß√£o do novo pet.
            novoPet.setMedicacao(novoMedicacao);
            // Define o hist√≥rico m√©dico do novo pet.
            novoPet.setHistoricoMedico(novoHistoricoMedico);

            // Cria uma inst√¢ncia do DAO (Data Access Object) para interagir com a tabela de pets.
            PetsDao petsDao = new PetsDao();
            // Cria uma inst√¢ncia do DAO para interagir com a tabela de tutores.
            TutorDao tutorDao = new TutorDao();

            // Usa o ID do tutor para buscar o objeto Tutor completo no banco de dados.
            Tutor tutorDoBanco = tutorDao.findById(novoIdTutor);

            // Verifica se a busca pelo tutor retornou um objeto nulo (ou seja, se o tutor n√£o foi encontrado).
            if (tutorDoBanco == null) {
                // Se o tutor n√£o existe, informa o usu√°rio com uma mensagem de erro.
                JOptionPane.showMessageDialog(this, "O Tutor com o ID informado (" + novoIdTutor + ") n√£o foi encontrado.",
                        "Erro de Valida√ß√£o", JOptionPane.ERROR_MESSAGE);
                // E interrompe a execu√ß√£o para n√£o tentar salvar um pet sem tutor.
                return;
            }

            // üéØ Ponto principal: Associa o objeto Tutor (encontrado) ao objeto Pet.
            // O JPA usar√° essa associa√ß√£o para salvar a chave estrangeira correta.
            novoPet.setTutor(tutorDoBanco);

            // Chama o m√©todo 'salvar' do DAO, que ir√° persistir o objeto 'novoPet' no banco de dados.
            petsDao.salvar(novoPet);

            // --- SE√á√ÉO 3: FEEDBACK E LIMPEZA ---
            // Exibe uma mensagem de sucesso para o usu√°rio.
            JOptionPane.showMessageDialog(this, "Pet cadastrado com sucesso!");
            // Chama um m√©todo (que voc√™ deve ter criado) para limpar os campos da tela.
            limparCampos();

// Se a convers√£o de String para n√∫mero (parseDouble ou parseInt) falhar, este bloco √© executado.
        } catch (NumberFormatException e) {
            // Informa ao usu√°rio que o formato do n√∫mero est√° incorreto.
            JOptionPane.showMessageDialog(this, "O valor para Peso ou ID do Tutor n√£o √© um n√∫mero v√°lido.",
                    "Erro de Formato", JOptionPane.ERROR_MESSAGE);
            // Imprime os detalhes t√©cnicos do erro no console, √∫til para o desenvolvedor.
            e.printStackTrace();

// Se ocorrer qualquer outro tipo de erro n√£o previsto acima (ex: problema de conex√£o com o BD).
        } catch (Exception e) {
            // Exibe uma mensagem de erro gen√©rica para o usu√°rio, contendo o motivo do erro.
            JOptionPane.showMessageDialog(this, "Ocorreu um erro inesperado: " + e.getMessage(),
                    "Erro", JOptionPane.ERROR_MESSAGE);
            // Imprime os detalhes t√©cnicos do erro no console.
            e.printStackTrace();
        }

    }//GEN-LAST:event_btCadastrarActionPerformed

